mapper.select:
/*MemberMapper.getMemberList*/
SELECT
    MEMBER_ID,
    USER_ID,
    PASSCODE,
    USER_NAME,
    TELL_NUMBER,
    SYS_REG_DATE,
    SYS_REG_ID,
    SYS_MOD_DATE,
    SYS_MOD_ID,
    STATUS,
    NOTE
FROM MEMBER
WHERE 1=1

    AND MEMBER_ID = #{memberId}


    AND USER_ID = #{userId}


    AND DATE_FORMAT(SYS_REG_DATE, '%Y%m%d') <= #{viewStdDate}


    AND DATE_FORMAT(SYS_REG_DATE, '%Y%m%d') >= #{viewEndDate}


    AND STATUS = #{status}


    AND TELL_NUMBER = #{tellNumber}


    AND USER_NAME = #{userName}
